mapper.select:


     and p.create_by = #{q.createBy}
     and p.purchase_no = #{q.purchaseNo}
     and p.purchase_status = #{q.purchaseStatus}
     and p.supplier_id = #{q.supplierId}
     and ss.supplier_name like concat('%', #{q.supplierName}, '%')

        and p.create_time between DATE_FORMAT(#{q.createBegin},'%Y-%m-%d 00:00:00') and DATE_FORMAT(#{q.createEnd},'%Y-%m-%d 23:59:59')


        and exists(select 1 from scm_purchase_detail pd where pd.purchase_id=p.id and pd.product_id=#{q.productId})


        and exists(select 1 from scm_purchase_detail pd left join pms_product pp on pp.id=pd.product_id
        where pd.purchase_id=p.id and pp.name like concat('%', #{q.productName}, '%'))


order by p.id desc
