mapper.select:



        and p.create_time between DATE_FORMAT(#{q.createBegin},'%Y-%m-%d 00:00:00') and DATE_FORMAT(#{q.createEnd},'%Y-%m-%d 23:59:59')

     and p.create_by = #{q.createBy}
     and p.receive_no = #{q.receiveNo}
     and p.receive_status = #{q.receiveStatus}
     and p.receive_no = #{q.receiveNo}
     and p.biz_type = #{q.bizType}
     and p.biz_no = #{q.bizNo}
     and p.logistics_name = #{q.logisticsName}
     and p.logistics_no = #{q.logisticsNo}

        and p.signed_time between DATE_FORMAT(#{q.signedBegin},'%Y-%m-%d 00:00:00') and DATE_FORMAT(#{q.signedEnd},'%Y-%m-%d 23:59:59')

     and p.batch_no = #{q.batchNo}

        and exists(select 1 from wms_receive_detail wrd where wrd.receive_id=p.id and wrd.product_id=#{q.productId})


        and exists(select 1 from wms_receive_detail wrd left join pms_product pp on pp.id=wrd.product_id
        where wrd.receive_id=p.id and pp.product_name like concat('%', #{q.productName}, '%'))


order by p.id desc
